SubPointSolutions.Docs.Code.Data.DocSample.FromXml("<?xml version=\"1.0\" encoding=\"utf-16\"?>\r\n<DocSample xmlns:xsi=\"http://www.w3.org/2001/XMLSchema-instance\" xmlns:xsd=\"http://www.w3.org/2001/XMLSchema\">\r\n  <IsMethod>false</IsMethod>\r\n  <IsClass>true</IsClass>\r\n  <Tags>\r\n    <DocSampleTag>\r\n      <Name>SharePoint Runtime</Name>\r\n      <Values>\r\n        <string>SharePoint Foundation</string>\r\n        <string>SharePoint Foundation</string>\r\n      </Values>\r\n    </DocSampleTag>\r\n    <DocSampleTag>\r\n      <Name>Sample M2 Model</Name>\r\n      <Values>\r\n        <string>Site Collection Model</string>\r\n        <string>Site Collection Model</string>\r\n      </Values>\r\n    </DocSampleTag>\r\n    <DocSampleTag>\r\n      <Name>Sample Category</Name>\r\n      <Values>\r\n        <string>Site collection</string>\r\n        <string>Site collection</string>\r\n      </Values>\r\n    </DocSampleTag>\r\n  </Tags>\r\n  <Title>WebDefinitionTest</Title>\r\n  <Description />\r\n  <MethodBodyWithFunction>    \r\n    \r\n\r\n    \r\n    \r\n    //[SampleMetadataTag(Name = BuiltInTagNames.SampleHidden)]\n    public class WebDefinitionTest : ProvisionTestBase\n    {\n        #region methods\n\n        [TestMethod]\n        [TestCategory(\"Docs.WebDefinition\")]\r\n\r\n\r\n        [SampleMetadata(Title = \"Add web\",\r\n                    Description = \"\"\r\n                    )]\r\n        //[SampleMetadataTag(Name = BuiltInTagNames.SampleHidden)]\n        public void CanDeploySimpleWeb()\n        {\n            var newCustomerWeb = new WebDefinition\n            {\n                Title = \"New customer site\",\n                Description = \"A dedicated site for the customer support.\",\n                Url = \"new-customer-web\",\n                WebTemplate = BuiltInWebTemplates.Collaboration.TeamSite\n            };\n            var newPublishingWeb = new WebDefinition\n            {\n                Title = \"Temporary Publishing Web\",\n                Description = \"A temporary punlishing web.\",\n                Url = \"new-publishing-web\",\n                WebTemplate = BuiltInWebTemplates.Publishing.PublishingPortal\n            };\n\n            var model = SPMeta2Model.NewWebModel(web =&gt;\n            {\n                web.AddWeb(newCustomerWeb);\n                web.AddWeb(newPublishingWeb);\n            });\n\n            DeployModel(model);\n        }\n\n\n        [TestMethod]\n        [TestCategory(\"Docs.WebDefinition\")]\r\n        [SampleMetadata(Title = \"Add multiple webs\",\r\n                    Description = \"\"\r\n                    )]\r\n        //[SampleMetadataTag(Name = BuiltInTagNames.SampleHidden)]\n        public void CanDeploySimpleWebs()\n        {\n            var model = SPMeta2Model.NewWebModel(web =&gt;\n            {\n                web.AddWeb(DocWebs.News);\n                web.AddWeb(DocWebs.AboutOurCompany);\n            });\n\n            DeployModel(model);\n        }\n\n\n\n        [TestMethod]\n        [TestCategory(\"Docs.WebDefinition\")]\r\n\r\n        [SampleMetadata(Title = \"Add hierarchical webs\",\r\n                    Description = \"\"\r\n                    )]\r\n        //[SampleMetadataTag(Name = BuiltInTagNames.SampleHidden)]\n\n        public void CanDeployHierarchicalWebs()\n        {\n            var model = SPMeta2Model.NewWebModel(web =&gt;\n            {\n                web\n                    .AddWeb(DocWebs.News)\n                    .AddWeb(DocWebs.Departments, departmentWeb =&gt;\n                    {\n                        departmentWeb\n                            .AddWeb(DocWebs.DepartmentWebs.HR)\n                            .AddWeb(DocWebs.DepartmentWebs.ITHelpDesk, itWeb =&gt;\n                            {\n                                itWeb\n                                    .AddWeb(DocWebs.DepartmentWebs.ITHelpDeskWebs.Apple)\n                                    .AddWeb(DocWebs.DepartmentWebs.ITHelpDeskWebs.Cisco)\n                                    .AddWeb(DocWebs.DepartmentWebs.ITHelpDeskWebs.Microsoft);\n                            })\n                            .AddWeb(DocWebs.DepartmentWebs.Sales);\n                    })\n                    .AddWeb(DocWebs.AboutOurCompany);\n            });\n\n            DeployModel(model);\n        }\n\n        #endregion\n    }</MethodBodyWithFunction>\r\n  <MethodBody>//[SampleMetadataTag(Name = BuiltInTagNames.SampleHidden)]\r\npublic class WebDefinitionTest : ProvisionTestBase\r\n{\r\n    #region methods\r\n\r\n    [TestMethod]\r\n    [TestCategory(\"Docs.WebDefinition\")]\r\n\r\n\r\n    [SampleMetadata(Title = \"Add web\",\r\n                Description = \"\"\r\n                )]\r\n    //[SampleMetadataTag(Name = BuiltInTagNames.SampleHidden)]\r\n    public void CanDeploySimpleWeb()\r\n    {\r\n        var newCustomerWeb = new WebDefinition\r\n        {\r\n            Title = \"New customer site\",\r\n            Description = \"A dedicated site for the customer support.\",\r\n            Url = \"new-customer-web\",\r\n            WebTemplate = BuiltInWebTemplates.Collaboration.TeamSite\r\n        };\r\n        var newPublishingWeb = new WebDefinition\r\n        {\r\n            Title = \"Temporary Publishing Web\",\r\n            Description = \"A temporary punlishing web.\",\r\n            Url = \"new-publishing-web\",\r\n            WebTemplate = BuiltInWebTemplates.Publishing.PublishingPortal\r\n        };\r\n\r\n        var model = SPMeta2Model.NewWebModel(web =&gt;\r\n        {\r\n            web.AddWeb(newCustomerWeb);\r\n            web.AddWeb(newPublishingWeb);\r\n        });\r\n\r\n        DeployModel(model);\r\n    }\r\n\r\n\r\n    [TestMethod]\r\n    [TestCategory(\"Docs.WebDefinition\")]\r\n    [SampleMetadata(Title = \"Add multiple webs\",\r\n                Description = \"\"\r\n                )]\r\n    //[SampleMetadataTag(Name = BuiltInTagNames.SampleHidden)]\r\n    public void CanDeploySimpleWebs()\r\n    {\r\n        var model = SPMeta2Model.NewWebModel(web =&gt;\r\n        {\r\n            web.AddWeb(DocWebs.News);\r\n            web.AddWeb(DocWebs.AboutOurCompany);\r\n        });\r\n\r\n        DeployModel(model);\r\n    }\r\n\r\n\r\n\r\n    [TestMethod]\r\n    [TestCategory(\"Docs.WebDefinition\")]\r\n\r\n    [SampleMetadata(Title = \"Add hierarchical webs\",\r\n                Description = \"\"\r\n                )]\r\n    //[SampleMetadataTag(Name = BuiltInTagNames.SampleHidden)]\r\n\r\n    public void CanDeployHierarchicalWebs()\r\n    {\r\n        var model = SPMeta2Model.NewWebModel(web =&gt;\r\n        {\r\n            web\r\n                .AddWeb(DocWebs.News)\r\n                .AddWeb(DocWebs.Departments, departmentWeb =&gt;\r\n                {\r\n                    departmentWeb\r\n                        .AddWeb(DocWebs.DepartmentWebs.HR)\r\n                        .AddWeb(DocWebs.DepartmentWebs.ITHelpDesk, itWeb =&gt;\r\n                        {\r\n                            itWeb\r\n                                .AddWeb(DocWebs.DepartmentWebs.ITHelpDeskWebs.Apple)\r\n                                .AddWeb(DocWebs.DepartmentWebs.ITHelpDeskWebs.Cisco)\r\n                                .AddWeb(DocWebs.DepartmentWebs.ITHelpDeskWebs.Microsoft);\r\n                        })\r\n                        .AddWeb(DocWebs.DepartmentWebs.Sales);\r\n                })\r\n                .AddWeb(DocWebs.AboutOurCompany);\r\n        });\r\n\r\n        DeployModel(model);\r\n    }\r\n\r\n    #endregion\r\n}\r\n</MethodBody>\r\n  <Language>cs</Language>\r\n  <Namespace>SPMeta2.Docs.ProvisionSamples.Provision.Definitions</Namespace>\r\n  <ClassName>WebDefinitionTest</ClassName>\r\n  <MethodName>WebDefinitionTestClass</MethodName>\r\n  <ClassComment />\r\n  <MethodFullName>ClassWebDefinitionTestClass</MethodFullName>\r\n  <ClassFullName>SPMeta2.Docs.ProvisionSamples.Provision.Definitions.WebDefinitionTest</ClassFullName>\r\n  <MethodParametersCount>0</MethodParametersCount>\r\n  <SourceFileName>WebDefinitionTests.cs</SourceFileName>\r\n  <SourceFileNameWithoutExtension>WebDefinitionTests</SourceFileNameWithoutExtension>\r\n  <SourceFileFolder>..\\..\\Views\\SPMeta2\\reference\\sp-foundation-definitions</SourceFileFolder>\r\n  <SourceFilePath>..\\..\\Views\\SPMeta2\\reference\\sp-foundation-definitions\\WebDefinitionTests.cs</SourceFilePath>\r\n</DocSample>")            ,
SubPointSolutions.Docs.Code.Data.DocSample.FromXml("<?xml version=\"1.0\" encoding=\"utf-16\"?>\r\n<DocSample xmlns:xsi=\"http://www.w3.org/2001/XMLSchema-instance\" xmlns:xsd=\"http://www.w3.org/2001/XMLSchema\">\r\n  <IsMethod>false</IsMethod>\r\n  <IsClass>true</IsClass>\r\n  <Tags>\r\n    <DocSampleTag>\r\n      <Name>Sample Category</Name>\r\n      <Values>\r\n        <string>Web parts</string>\r\n        <string>Web parts</string>\r\n      </Values>\r\n    </DocSampleTag>\r\n    <DocSampleTag>\r\n      <Name>Sample M2 Model</Name>\r\n      <Values>\r\n        <string>Web Model</string>\r\n        <string>Web Model</string>\r\n      </Values>\r\n    </DocSampleTag>\r\n    <DocSampleTag>\r\n      <Name>SharePoint Runtime</Name>\r\n      <Values>\r\n        <string>SharePoint Foundation</string>\r\n        <string>SharePoint Foundation</string>\r\n      </Values>\r\n    </DocSampleTag>\r\n  </Tags>\r\n  <Title>WebPartDefinitionTests</Title>\r\n  <Description />\r\n  <MethodBodyWithFunction>    \r\n    \r\n\r\n    \r\n    \r\n    //[SampleMetadataTag(Name = BuiltInTagNames.SampleHidden)]\n    public class WebPartDefinitionTests : ProvisionTestBase\n    {\n        #region methods\n\n        [TestMethod]\n        [TestCategory(\"Docs.WebPartDefinition\")]\r\n\r\n        [SampleMetadata(Title = \"Add web part by type\",\r\n                    Description = \"\"\r\n                    )]\r\n        //[SampleMetadataTag(Name = BuiltInTagNames.SampleHidden)]\n\n        public void CanDeployWebpartByWebpartType()\n        {\n            // this would deploy a web part using WebpartType prop\n            // you need to provide AssemblyQualifiedName of the target web part type\n            // M2 would use reflection to create an instane of the web part in the runtime\n            // that works only for SSOM, not CSOM support yet\n\n            var contentEditorWebPart = new WebPartDefinition\n            {\n                Title = \"About SharePoint SSOM\",\n                Id = \"m2AboutSharePointSSOM\",\n                WebpartType = typeof(ContentEditorWebPart).AssemblyQualifiedName,\n                ZoneIndex = 10,\n                ZoneId = \"Main\"\n            };\n\n            var webPartPage = new WebPartPageDefinition\n            {\n                Title = \"M2 webparts provision\",\n                FileName = \"web-parts-provision.aspx\",\n                PageLayoutTemplate = BuiltInWebPartPageTemplates.spstd1\n            };\n\n            var model = SPMeta2Model.NewWebModel(web =&gt;\n            {\n                web.AddHostList(BuiltInListDefinitions.SitePages, list =&gt;\n                {\n                    list.AddWebPartPage(webPartPage, page =&gt;\n                    {\n                        page.AddWebPart(contentEditorWebPart);\n                    });\n                });\n            });\n\n            DeploySSOMModel(model);\n        }\n\n        [TestMethod]\n        [TestCategory(\"Docs.WebPartDefinition\")]\r\n\r\n\r\n        [SampleMetadata(Title = \"Add web part by XML\",\r\n                    Description = \"\"\r\n                    )]\r\n        //[SampleMetadataTag(Name = BuiltInTagNames.SampleHidden)]\n        public void CanDeployWebpartByXML()\n        {\n            // this whould deploy the web part using WebpartXmlTemplate prop\n            // you need to provide an XML template which you get from SharePoint\n            // export the wenb part, and put it into WebpartXmlTemplate prop\n\n            // here is a web part XML template\n            // usually, you export that XML from SharePoint page, but M2 has pre-build class\n            var contentEditorWebPartXml = BuiltInWebPartTemplates.ContentEditorWebPart;\n\n            var contentEditorWebPart = new WebPartDefinition\n            {\n                Title = \"About SharePoint XML\",\n                Id = \"m2AboutSharePointXML\",\n                WebpartXmlTemplate = contentEditorWebPartXml,\n                ZoneIndex = 20,\n                ZoneId = \"Main\"\n            };\n\n            var webPartPage = new WebPartPageDefinition\n            {\n                Title = \"M2 webparts provision\",\n                FileName = \"web-parts-provision.aspx\",\n                PageLayoutTemplate = BuiltInWebPartPageTemplates.spstd1\n            };\n\n            var model = SPMeta2Model.NewWebModel(web =&gt;\n            {\n                web.AddHostList(BuiltInListDefinitions.SitePages, list =&gt;\n                {\n                    list.AddWebPartPage(webPartPage, page =&gt;\n                    {\n                        page.AddWebPart(contentEditorWebPart);\n                    });\n                });\n            });\n\n            DeployModel(model);\n        }\n\n        [TestMethod]\n        [TestCategory(\"Docs.WebPartDefinition\")]\r\n\r\n        [SampleMetadata(Title = \"Add web part from Gallery File\",\r\n                    Description = \"\"\r\n                    )]\r\n        //[SampleMetadataTag(Name = BuiltInTagNames.SampleHidden)]\n        public void CanDeployWebpartByWebpartGalleryFileName()\n        {\n            // this would deploy the web part using WebpartFileName\n            // you need to provide a file name ofthe web part template in the web part gallery\n            // M2 would load this file, then use an XML as a web part template\n\n            var contentEditorWebPart = new WebPartDefinition\n            {\n                Title = \"About SharePoint web part gallery\",\n                Id = \"m2AboutSharePointWebPartGallery\",\n                // shortcut to \"MSContentEditor.dwp\",\n                WebpartFileName = BuiltInWebpartFileNames.MSContentEditor,\n                ZoneIndex = 20,\n                ZoneId = \"Main\"\n            };\n\n            var webPartPage = new WebPartPageDefinition\n            {\n                Title = \"M2 webparts provision\",\n                FileName = \"web-parts-provision.aspx\",\n                PageLayoutTemplate = BuiltInWebPartPageTemplates.spstd1\n            };\n\n            var model = SPMeta2Model.NewWebModel(web =&gt;\n            {\n                web.AddHostList(BuiltInListDefinitions.SitePages, list =&gt;\n                {\n                    list.AddWebPartPage(webPartPage, page =&gt;\n                    {\n                        page.AddWebPart(contentEditorWebPart);\n                    });\n                });\n            });\n\n            DeployModel(model);\n        }\n\n        [TestMethod]\n        [TestCategory(\"Docs.WebPartDefinition\")]\r\n\r\n        [SampleMetadata(Title = \"Add web part with pre-configured XML\",\r\n                    Description = \"\"\r\n                    )]\r\n        //[SampleMetadataTag(Name = BuiltInTagNames.SampleHidden)]\n\n        public void CanDeployWebpartWithPreprocessedXML()\n        {\n            // this shows how to use M2 API to pre-process web part XML\n\n            // here is a web part XML template\n            // usually, you export that XML from SharePoint page, but M2 has pre-build class\n            var contentEditorWebPartXml = BuiltInWebPartTemplates.ContentEditorWebPart;\n\n            // let\' set new some properties, shall we?\n            // we load XML by WebpartXmlExtensions.LoadWebpartXmlDocument() method\n            // it works well web both V2/V3 web part XML\n            // then change properties and seehow it goes\n            // then call ToString() to get string out of XML\n            var wpXml = WebpartXmlExtensions\n                           .LoadWebpartXmlDocument(contentEditorWebPartXml)\n                           .SetOrUpdateProperty(\"FrameType\", \"Standard\")\n                           .SetOrUpdateProperty(\"Width\", \"500\")\n                           .SetOrUpdateProperty(\"Heigth\", \"200\")\n                           .SetOrUpdateContentEditorWebPartProperty(\"Content\", \"This is a new content!\", true)\n                           .ToString();\n\n            var