SubPointSolutions.Docs.Code.Data.DocSample.FromXml("<?xml version=\"1.0\" encoding=\"utf-16\"?>\r\n<DocSample xmlns:xsi=\"http://www.w3.org/2001/XMLSchema-instance\" xmlns:xsd=\"http://www.w3.org/2001/XMLSchema\">\r\n  <IsMethod>true</IsMethod>\r\n  <IsClass>false</IsClass>\r\n  <Tags />\r\n  <Title>CorrectSPFileExistsUsage</Title>\r\n  <Description />\r\n  <MethodBodyWithFunction>\r\n        public void CorrectSPFileExistsUsage(SPWeb web)\r\n        {\r\n            var defaultMasterUrl = \"/_catalogs/masterpage/default.master\";\r\n\r\n            if (web.AllProperties.ContainsKey(\"OldMasterUrl\"))\r\n            {\r\n                string oldMasterUrl = web.AllProperties[\"OldMasterUrl\"].ToString();\r\n                try\r\n                {\r\n                    var fileExists = web.GetFile(oldMasterUrl).Exists;\r\n                    web.MasterUrl = oldMasterUrl;\r\n                }\r\n                catch (ArgumentException)\r\n                {\r\n                    web.MasterUrl = defaultMasterUrl;\r\n                }\r\n\r\n\r\n                var oldCustomUrl = web.AllProperties[\"OldCustomMasterUrl\"].ToString();\r\n                try\r\n                {\r\n                    var fileExists = web.GetFile(oldCustomUrl).Exists;\r\n                    web.CustomMasterUrl = web.AllProperties[\"OldCustomMasterUrl\"].ToString();\r\n                }\r\n                catch (ArgumentException)\r\n                {\r\n                    web.CustomMasterUrl = defaultMasterUrl;\r\n                }\r\n\r\n                web.AllProperties.Remove(\"OldMasterUrl\");\r\n                web.AllProperties.Remove(\"OldCustomMasterUrl\");\r\n            }\r\n            else\r\n            {\r\n                web.MasterUrl = defaultMasterUrl;\r\n                web.CustomMasterUrl = defaultMasterUrl;\r\n            }\r\n        }</MethodBodyWithFunction>\r\n  <MethodBody>var defaultMasterUrl = \"/_catalogs/masterpage/default.master\";\r\n\r\nif (web.AllProperties.ContainsKey(\"OldMasterUrl\"))\r\n{\r\n    string oldMasterUrl = web.AllProperties[\"OldMasterUrl\"].ToString();\r\n    try\r\n    {\r\n        var fileExists = web.GetFile(oldMasterUrl).Exists;\r\n        web.MasterUrl = oldMasterUrl;\r\n    }\r\n    catch (ArgumentException)\r\n    {\r\n        web.MasterUrl = defaultMasterUrl;\r\n    }\r\n\r\n\r\n    var oldCustomUrl = web.AllProperties[\"OldCustomMasterUrl\"].ToString();\r\n    try\r\n    {\r\n        var fileExists = web.GetFile(oldCustomUrl).Exists;\r\n        web.CustomMasterUrl = web.AllProperties[\"OldCustomMasterUrl\"].ToString();\r\n    }\r\n    catch (ArgumentException)\r\n    {\r\n        web.CustomMasterUrl = defaultMasterUrl;\r\n    }\r\n\r\n    web.AllProperties.Remove(\"OldMasterUrl\");\r\n    web.AllProperties.Remove(\"OldCustomMasterUrl\");\r\n}\r\nelse\r\n{\r\n    web.MasterUrl = defaultMasterUrl;\r\n    web.CustomMasterUrl = defaultMasterUrl;\r\n}\r\n\r\n</MethodBody>\r\n  <Language>cs</Language>\r\n  <Namespace>ReSharePoint.Docs.Basic.Inspection.Code</Namespace>\r\n  <ClassName>PutSPFileExistsIntoTryCatchBlock</ClassName>\r\n  <MethodName>CorrectSPFileExistsUsage</MethodName>\r\n  <ClassComment />\r\n  <MethodFullName>ReSharePoint.Docs.Basic.Inspection.Code.PutSPFileExistsIntoTryCatchBlock.CorrectSPFileExistsUsage</MethodFullName>\r\n  <ClassFullName>ReSharePoint.Docs.Basic.Inspection.Code.PutSPFileExistsIntoTryCatchBlock</ClassFullName>\r\n  <MethodParametersCount>1</MethodParametersCount>\r\n  <SourceFileName>PutSPFileExistsIntoTryCatchBlock.cs</SourceFileName>\r\n  <SourceFileNameWithoutExtension>PutSPFileExistsIntoTryCatchBlock</SourceFileNameWithoutExtension>\r\n  <SourceFileFolder>..\\..\\Views\\reSP\\Inspections\\csharp</SourceFileFolder>\r\n  <SourceFilePath>..\\..\\Views\\reSP\\Inspections\\csharp\\PutSPFileExistsIntoTryCatchBlock.cs</SourceFilePath>\r\n</DocSample>")            ,
